import React, { useState } from 'react';
import 'react-image-gallery/styles/css/image-gallery.css';

const images = [
  {
    original: require('./galleryimages/banner.jpg'),
    thumbnail: require('./galleryimages/banner.jpg'),
    // Other image properties like alt text, caption, etc.
  },
  {
    original: require('./galleryimages/heart.png'),
    thumbnail: require('./galleryimages/heart.png'),
    // Other image properties like alt text, caption, etc.
  },
  {
    original: require('./galleryimages/rad.png'),
    thumbnail: require('./galleryimages/rad.png'),
    // Other image properties like alt text, caption, etc.
  },
];

const Gallery = () => {
  const [showOriginalImage, setShowOriginalImage] = useState(false);
  const [selectedImage, setSelectedImage] = useState(null);

  const handleThumbnailClick = (item) => {
    setShowOriginalImage(true);
    setSelectedImage(item.original);
  };

  const handleOriginalImageClose = () => {
    setShowOriginalImage(false);
    setSelectedImage(null);
  };

  return (
    <div className="gallery">
      {showOriginalImage && (
        <div className="gallery-modal" onClick={handleOriginalImageClose}>
          <img src={selectedImage} alt="Original" className="gallery-original-image" />
        </div>
      )}
      <div className="gallery-thumbnails">
        {images.map((item, index) => (
          <img
            key={index}
            src={item.thumbnail}
            alt={`Thumbnail ${index}`}
            className="gallery-thumbnail"
            onClick={() => handleThumbnailClick(item)}
          />
        ))}
      </div>
    </div>
  );
};

export default Gallery;
