import React from 'react';
import ImageGallery from 'react-image-gallery';
import 'react-image-gallery/styles/css/image-gallery.css';
import './Gallery.css'; // Import the CSS file


const images = [
  {
    original: require('./galleryimages/banner.jpg'),
    thumbnail: require('./galleryimages/banner.jpg'),
    // Other image properties like alt text, caption, etc.
  },
  {
    original: require('./galleryimages/heart.png'),
    thumbnail: require('./galleryimages/heart.png'),
    // Other image properties like alt text, caption, etc.
  },
  {
    original: require('./galleryimages/rad.png'),
    thumbnail: require('./galleryimages/rad.png'),
    // Other image properties like alt text, caption, etc.
  },
];

const Gallery = () => {
  const renderItem = (item) => {
    return (
      <div className="gallery-thumbnail">
        <img src={item.thumbnail} aat={item.originalAlt} onClick={() => handleThumbnailClick(item)} />
      </div>
    );
  };

  const handleThumbnailClick = (item) => {
    // Handle the click event to load the full-size image
    console.log('Clicked on thumbnail:', item.original);
    // You can display the full-size image in a modal or a separate section on the screen
  };

  return (
    <div className="gallery">
      <ImageGallery items={images} renderItem={renderItem} showPlayButton={false} showFullscreenButton={false} />
    </div>
  );
};

export default Gallery;
