import React, { useState } from 'react';
import Modal from 'react-modal';
import './Gallery.css';
import radImage from './galleryimages/rad.jpg';

export default function Gallery() {
  const [modalIsOpen, setModalIsOpen] = useState(false);
  const [selectedImage, setSelectedImage] = useState(null);

  const openModal = (image) => {
    setSelectedImage(image);
    setModalIsOpen(true);
  };

  const closeModal = () => {
    setSelectedImage(null);
    setModalIsOpen(false);
  };

  return (
    <div>
      <h1>Photo Gallery grid</h1>
      <div className="photo-grid">
        <div
          className="card card-tall card-wide"
          style={{
            backgroundImage: `url(${radImage})`,
          }}
          onClick={() => openModal(radImage)}
        >
          1
        </div>
        <div
          className="card card-tall"
          style={{
            backgroundImage:
              "url('https://images.unsplash.com/photo-1583585635793-0e1894c169bd?ixlib=rb-1.2.1&ixid=eyJhcHBfaWQiOjEyMDd9&auto=format&fit=crop&w=913&q=80')",
          }}
          onClick={() =>
            openModal(
              'https://images.unsplash.com/photo-1583585635793-0e1894c169bd?ixlib=rb-1.2.1&ixid=eyJhcHBfaWQiOjEyMDd9&auto=format&fit=crop&w=913&q=80'
            )
          }
        >
          2
        </div>
        {/* Add the remaining cards with their respective click handlers */}
        {/* Example:
        <div
          className="card"
          style={{
            backgroundImage:
              "url('https://images.unsplash.com/photo-XXXXX')",
          }}
          onClick={() =>
            openModal('https://images.unsplash.com/photo-XXXXX')
          }
        >
          X
        </div>
        */}
      </div>

      {selectedImage && (
        <Modal
          isOpen={modalIsOpen}
          onRequestClose={closeModal}
          contentLabel="Enlarged Image"
          className="modal"
          overlayClassName="modal-overlay"
        >
          <img src={selectedImage} alt="Enlarged" />
        </Modal>
      )}
    </div>
  );
}
