import React, { useState } from 'react';
import ImageGallery from 'react-image-gallery';
import 'react-image-gallery/styles/css/image-gallery.css';
import './Gallery.css';
import Modal from 'react-modal';

const images = [
  {
    original: require('./galleryimages/banner.jpg'),
    thumbnail: require('./galleryimages/banner.jpg'),
    // Other image properties like alt text, caption, etc.
  },
  {
    original: require('./galleryimages/heart.png'),
    thumbnail: require('./galleryimages/heart.png'),
    // Other image properties like alt text, caption, etc.
  },
  {
    original: require('./galleryimages/rad.png'),
    thumbnail: require('./galleryimages/rad.png'),
    // Other image properties like alt text, caption, etc.
  },
];

const Gallery = () => {
  const [modalIsOpen, setModalIsOpen] = useState(false);
  const [selectedImageIndex, setSelectedImageIndex] = useState(0);

  const handleThumbnailClick = (index) => {
    setSelectedImageIndex(index);
    setModalIsOpen(true);
  };

  const handleCloseModal = () => {
    setModalIsOpen(false);
  };

  return (
    <div className="gallery">
      <ImageGallery
        items={images}
        renderItem={(item) => (
          <div className="gallery-thumbnail">
            <img
              src={item.thumbnail}
              alt={item.originalAlt}
              onClick={() => handleThumbnailClick(item.index)}
            />
          </div>
        )}
        showPlayButton={false}
        showFullscreenButton={false}
      />
      <Modal
        isOpen={modalIsOpen}
        onRequestClose={handleCloseModal}
        className="modal"
        overlayClassName="modal-overlay"
      >
        <div className="modal-content">
          <button className="close-button" onClick={handleCloseModal}>
            Close
          </button>
          <img
            className="modal-image"
            src={images[selectedImageIndex].original}
            alt={images[selectedImageIndex].originalAlt}
          />
        </div>
      </Modal>
    </div>
  );
};

export default Gallery;
